import React, { useState, useEffect } from 'react';
import { Employee } from '../types';

interface EmployeeFormProps {
    onSubmit: (data: Employee) => void;
    employee?: Employee | null;
}

const EmployeeForm: React.FC<EmployeeFormProps> = ({ onSubmit, employee }) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [position, setPosition] = useState('');
    const [departmentId, setDepartmentId] = useState('');

    useEffect(() => {
        if (employee) {
            setName(employee.name || '');
            setEmail(employee.email || '');
            setPosition(employee.position || '');
            setDepartmentId(employee.departmentId?.toString() || '');
        } else {
            setName('');
            setEmail('');
            setPosition('');
            setDepartmentId('');
        }
    }, [employee]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        const newEmployee: Employee = {
            id: employee?.id || 0,
            name,
            email,
            position,
            departmentId: parseInt(departmentId) || 0,
            dateOfJoining: employee?.dateOfJoining || new Date().toISOString().split('T')[0],
        };
        onSubmit(newEmployee);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div>
                <label>Position:</label>
                <input type="text" value={position} onChange={(e) => setPosition(e.target.value)} required />
            </div>
            <div>
                <label>Department ID:</label>
                <input type="number" value={departmentId} onChange={(e) => setDepartmentId(e.target.value)} />
            </div>
            <button type="submit">Submit</button>
        </form>
    );
};

export default EmployeeForm;
