import React, { useEffect, useState } from 'react';
import EmployeeList from '../components/EmployeeList';
import EmployeeForm from '../components/EmployeeForm';
import { Employee } from '../types';

const Employees: React.FC = () => {
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);

    useEffect(() => {
        const fetchEmployees = async () => {
            const response = await fetch('/api/employees');
            const data = await response.json();
            setEmployees(data);
        };

        fetchEmployees();
    }, []);

    const handleEdit = (employee: Employee) => {
        setSelectedEmployee(employee);
    };

    const handleDelete = async (id: number) => {
        await fetch(`/api/employees/${id}`, {
            method: 'DELETE',
        });
        setEmployees(employees.filter(emp => emp.id !== id));
    };

    const handleFormSubmit = async (employee: Employee) => {
        if (selectedEmployee) {
            await fetch(`/api/employees/${selectedEmployee.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(employee),
            });
        } else {
            await fetch('/api/employees', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(employee),
            });
        }
        setSelectedEmployee(null);
        const response = await fetch('/api/employees');
        const data = await response.json();
        setEmployees(data);
    };

    return (
        <div>
            <h1>Employee Management</h1>
            <EmployeeForm employee={selectedEmployee} onSubmit={handleFormSubmit} />
            <EmployeeList employees={employees} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default Employees;
